import pytest
import test_func
import os

def test_save_img():
	x = test_func.save_img(['https://www.eltiempo.com/files/image_170_113/uploads/2022/11/18/63780fc6bd858.jpeg'],True)
	assert os.path.exists(x[0][0]) == True
	assert os.path.exists(test_func.save_img(['https://i.redd.it/b7tos3vtz51a1.png'],True)[0][0]) == True

